import React, {useState, useEffect} from 'react';
import axios from 'axios'
import { useNavigate, Link} from 'react-router-dom';

const Menu = () => {

    let [loggedInUser, setLoggedInUser] = useState({})
    let [allUsers, setAllUsers] = useState([])
    let [search, setSearch] = useState("")
    let [friendList, setFriendList] = useState([])
    const [seePosts, setSeePosts] = useState(false)

    const navigate = useNavigate()

    useEffect(() => {
        axios.get("http://localhost:8000/api/users/getloggedinuser", {withCredentials: true})
            .then(res =>{
                console.log(res)
                if(res.data.results){
                    setLoggedInUser(res.data.results)
                } 
            })
            .catch(err=>{
                console.log(err.response.data)
                navigate("/")
            })
        // eslint-disable-next-line
    }, [])

    useEffect(() => {
        axios.get("http://localhost:8000/api/users")
            .then(res =>{
                console.log(res)
                if(res.data.results){
                    setAllUsers(res.data.results)
                } 
            })
            .catch(err=>{
                console.log(err)
                navigate("/")
            })
            // eslint-disable-next-line
    }, [])

    const logout = () =>{
        axios.get("http://localhost:8000/api/users/logout", {withCredentials: true})
            .then(res =>{
                navigate("/")
            })
            .catch(err=> console.log(err))
    }

    const getFriends = () => {
        for (const friend of loggedInUser.friends){
            axios.get(`http://localhost:8000/api/users/get/${friend}`)
                .then(res => {
                    console.log(res)
                    if (res.data.results !== undefined ){
                        setFriendList([...friendList, res.data.results])
                    } 
                    console.log(res.data.results)
                })
                .catch(err =>{
                    console.log(err)
                })
        }
        setSeePosts(true)
    }

    const searching = (e) => {
        setSearch(e.target.value)
    }

    return (
        <div>
            <h1>Welcome {loggedInUser.firstName}</h1>
            <div>
                <button className='btn btn-info' onClick={logout}>Logout</button>
            </div>

            <div class="box">
                <form name="search">
                    <input type="text" placeholder="Search for people.." onChange={searching} className="search-bar" />
                </form>
                <i class="fas fa-search"></i>
            </div>

            {
                allUsers.map(user => {
                    return(
                        <div key={user._id}>
                            {search === user.firstName || search === user.lastName || search === user.firstName + " " + user.lastName ? (
                                <div>
                                    <img src={user.profilePicture} alt="" className="w-25" />
                                    <Link to={`/profile/${user._id}`}><h1>{user.firstName} {user.lastName}</h1></Link>
                                </div>
                            ) : (
                                <></>
                            )}
                        </div>)
                })
            }

            { !seePosts ? (<button onClick={getFriends}>Test</button>) : <></>}

            { seePosts ? (
                friendList.map(friend => {
                    return(
                        friend.posts.map(post =>{
                            return (
                                <div>
                                    <img src={post.imgUrl} alt="" className='w-25' />
                                    <h1>{post.caption}</h1>
                                </div>
                            )
                        })
                    )
                }) ) : ( <></>)
            }

        </div>
    );
};

export default Menu;
