import React, {useState, useEffect} from 'react';
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom';

const FriendProfile = () => {
    let [loggedInUser, setLoggedInUser] = useState({})
    let [otherUser, setOtherUser] = useState({})
    const [image, setImage] = useState({})
    const [form, setForm] = useState({})
    const [loading, setLoading] = useState(false)
    const [loadingPost, setLoadingPost] = useState(true)
    const [isPopup, setIsPopup] = useState(false)
    const [isPostPopup, setIsPostPopup] = useState(false)

    const {id} = useParams();

    const navigate = useNavigate()

    useEffect(() => {
        axios.get(`http://localhost:8000/api/users/get/${id}`, {withCredentials: true})
            .then(res =>{
                console.log(res)
                if(res.data.results){
                    setOtherUser(res.data.results)
                } 
                setLoadingPost(false)
            })
            .catch(err=>{
                console.log(err)
                navigate("/")
            })
            // eslint-disable-next-line
    }, [])

    const uploadImage = (e) => {
        console.log("upload image")
        const files = e.target.files
        const data = new FormData()
        data.append('file', files[0])
        data.append('upload_preset', 'facebook')
        setLoading(true)
        axios.post('https://api.cloudinary.com/v1_1/dyedz6zps/image/upload', data)
            .then(res => {
                console.log(res)
                setImage({
                    caption: "hello",
                    imgUrl: res.data.url
                })
                setLoading(false)
            })
            .catch(err=>{
                console.log(err)
                setLoading(false)
            })
    }

    const postPicture = (e) => {
        console.log("this is image", image)
        e.preventDefault()
        axios.post(`http://localhost:8000/api/users/${otherUser._id}/posts`, image)
            .then(res =>{
                console.log(res)
                closePostPopup()
            })
            .catch(err => {
                console.log(err)
            })
    }

    const captionHandler = (e) => {
        setImage({
            ...image,
            [e.target.name]: e.target.value
        })
    }

    const profileHandler = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }

    const uploadProfile = (e) => {
        console.log("upload image")
        const pFiles = e.target.files
        const profileData = new FormData()
        profileData.append('file', pFiles[0])
        profileData.append('upload_preset', 'facebook')

        axios.post('https://api.cloudinary.com/v1_1/dyedz6zps/image/upload', profileData)
            .then(res => {
                console.log(res)
                console.log(res.data.url)
                axios.put(`http://localhost:8000/api/users/${loggedInUser._id}`, {profilePicture: res.data.url})
                    .then(res => {
                        console.log(res)
                    })
                    .catch(err => {
                        console.log(err)
                    })
            })
            .catch(err=>{
                console.log(err)
            })
    }

    const uploadCover = (e) => {
        console.log("upload image")
        const cFiles = e.target.files
        const coverData = new FormData()
        coverData.append('file', cFiles[0])
        coverData.append('upload_preset', 'facebook')

        axios.post('https://api.cloudinary.com/v1_1/dyedz6zps/image/upload', coverData)
            .then(res => {
                console.log(res)
                console.log(res.data.url)
                axios.put(`http://localhost:8000/api/users/${loggedInUser._id}`, {coverPicture: res.data.url})
                    .then(res => {
                        console.log(res)
                    })
                    .catch(err => {
                        console.log(err)
                    })
            })
            .catch(err=>{
                console.log(err)
            })
    }

    const popup = () => {
        setIsPopup(true)
    }

    const closePopupForm = () => {
        axios.put(`http://localhost:8000/api/users/${loggedInUser._id}`, form)
            .then(res => {
                console.log(res)
                setIsPopup(false)
            })
            .catch(err => {
                console.log(err)
            })

    }

    const postPopup = () => {
        setIsPostPopup(true)
    }

    const closePostPopup = () => {
        setIsPostPopup(false)
    }

    const formatDate = (date) => {
        const newDate = new Date(date)
        return newDate.toLocaleDateString("en-US")
    }


    return (
        <div>

            <div className='head-container'>

                { otherUser.coverPicture === "" ? (
                    <img className="image-cover rounded" src="https://theoheartist.com/wp-content/uploads/sites/2/2015/01/fbdefault.png" alt = ""/>
                ) : (
                    <img className="image-cover rounded" src={otherUser.coverPicture} alt="" />
                )
                }


                { otherUser.profilePicture === "" ? (
                    <img className="rounded-circle image-profile text-center" src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png" alt = ""/>
                ) : (
                    <img className="rounded-circle image-profile text-center" src={otherUser.profilePicture} alt="" />
                )
                }


                <h1 className='text-center'>{otherUser.firstName} {otherUser.lastName}</h1>

                <div className="d-flex justify-content-evenly">
                    <h4>Lives in: {otherUser.location}</h4>
                        {formatDate(otherUser.birthday) === "11/11/1111" ?(
                            <h4>Birthday: Unknown </h4>
                        ) : (
                        <h4>Birthday: {formatDate(otherUser.birthday)}</h4>
                        )
                        }
                </div>

                <div className="text-center">
                    <button className='btn btn-info'>Add Friend</button>
                </div>


            </div>

            <div className='bg-light head-container d-flex'>
                <div class="col">
                    <h1>Friends</h1>
                </div>
                <div className="col">
                    <h1>Posts</h1>
                    { !loadingPost ? (

                        otherUser.posts.map(post => {
                            return(
                                <div key={post._id}>
                                    <img src={post.imgUrl} alt="none" className='w-25'/>
                                    <h5>{post.caption}</h5>
                                </div>
                            )
                        })
                    ) : (<h1>Loading...</h1>)
                    }
                </div>
            </div>
        </div>
    );
};

export default FriendProfile;
