import React, {useState, useEffect} from 'react';
import axios from 'axios'
import { useNavigate } from 'react-router-dom';

const Menu = () => {

    let [loggedInUser, setLoggedInUser] = useState({})

    const navigate = useNavigate()

    useEffect(() => {
        axios.get("http://localhost:8000/api/users/getloggedinuser", {withCredentials: true})
            .then(res =>{
                console.log(res)
                if(res.data.results){
                    setLoggedInUser(res.data.results)
                } 
            })
            .catch(err=>{
                console.log(err)
                navigate("/")
            })
            // eslint-disable-next-line
    }, [])

    const logout = () =>{
        axios.get("http://localhost:8000/api/users/logout", {withCredentials: true})
            .then(res =>{
                navigate("/")
            })
            .catch(err=> console.log(err))
    }


    const [image, setImage] = useState('')
    const [loading, setLoading] = useState(false)

    const uploadImage = (e) => {
        console.log("upload image")
        const files = e.target.files
        const data = new FormData()
        data.append('file', files[0])
        data.append('upload_preset', 'facebook')
        setLoading(true)
        axios.post('https://api.cloudinary.com/v1_1/dyedz6zps/image/upload', data)
            .then(res => {
                console.log(res)
                setImage(res.data.url)
                setLoading(false)
            })
            .catch(err=>{
                console.log(err)
                setLoading(false)
            })
    }

    const postPicture = (e) => {

        axios.post(`http://localhost:8000/api/users/${loggedInUser._id}/posts`, image)
            .then(res =>{
                console.log(res)
            })
            .catch(err => {
                console.log(err)
            })
    }

    return (
        <div>
            <h1>Welcome {loggedInUser.firstName}</h1>
            <button className='btn btn-info' onClick={logout}>Logout</button>
            <input
            type="file"
            name="file"
            placeholder="Upload an image"
            onChange={uploadImage}
            />
            {loading ? (
            <h3>Loading...</h3>
            ) : (
                <img src={image} style={{ width: '300px' }} alt="no img" />
            )}
            <button className="btn btn-info" onClick={postPicture}>Post</button>
            <h1>Posts</h1>
            {loggedInUser.posts.map((post) => <img src={post.imgUrl} alt="none" />)}
        </div>
    );
};

export default Menu;
